    function unpause() public virtual {
        require(hasRole(PAUSER_ROLE, _msgSender()), 'ERC721MinterPauser: must have pauser role to unpause');
        _unpause();
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
